{{ 'welcome' | ngccI18n: { name: 'Alice' } }}
\`\`\`
        `,
      },
    },
  },
};

export default meta;

// Setup Guide
@Component({
  selector: 'ngcc-i18n-setup-guide',
  standalone: true,
