    // Get with parameters
    const message = this.i18n.t('welcome', { name: 'Alice' });

    // Get current language
    const lang = this.i18n.getLanguage();

    // Check if key exists
    if (this.i18n.has('greeting')) {
      // ...
    }
  }

  switchLanguage(lang: string) {
