(startDate: string, length = 20) => {
  const start = new Date(startDate);
  return Array.from({ length }, (_, i) => {
    const d = new Date(start);
    d.setDate(start.getDate() + i);
    return d.toISOString().split('T')[0]; // YYYY-MM-DD
  });
}

